import { useState } from "react";
import "./App.css";

function App() {
  const [count,setcount]=useState(0)
  return (
    <div className="App">
      <h2 data-testid="counter-value">{count}</h2>
      <button data-testid="counter-decrement-button" disabled={count===0} onClick={()=>{
        setcount(count-1)
      }}>-</button>
      <button data-testid="counter-increment-button" onClick={()=>{
        setcount(count+1)
      }}></button>
    </div>
  );
}

export default App;
